document.addEventListener("DOMContentLoaded",function(){for(const n of document.querySelectorAll(".toggle-medidas"))n.addEventListener("click",e=>{e.preventDefault(),n.classList.contains("visible")?(n.classList.remove("visible"),n.nextElementSibling.style.display="none"):(n.classList.add("visible"),n.nextElementSibling.style.display="inline-block")})});